
public class ageIn2030 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String myName = "Isaac Mugomba";
		final int FUTURE_YEAR = 2030;
		final int CURRENT_YEAR = 2021;
		final int CURRENT_AGE = 18;
		int newAge = CURRENT_AGE + (FUTURE_YEAR-CURRENT_YEAR);
		System.out.print(myName);
		System.out.print(" will be age ");
		System.out.print(newAge);
		System.out.print(" in the year ");
		System.out.println(FUTURE_YEAR);
		
	}

}
